mapper.update:
UPDATE notice SET subject = #{subject}, content = #{content},
	notice = #{notice}, showNotice = #{showNotice}, modify_date = SYSDATE
WHERE num = #{num}

mapper.select:
SELECT fileNum, num, save_filename, original_filename, filesize
FROM notice_File
WHERE num = #{num}

SELECT fileNum, num, save_filename, original_filename, filesize
FROM notice_File
WHERE fileNum = #{fileNum}
